import React, { Component } from 'react';
import { Modal, ModalHeader, ModalBody, Button } from 'reactstrap';
import {  FadeTransform } from 'react-animation-components';
class Contact extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isModalOpen: false,
        };
        this.toggleModal = this.toggleModal.bind(this);
        this.showModal = this.showModal.bind(this);
    }

    toggleModal () {
        this.setState({
            isModalOpen: !this.state.isModalOpen
        })
    }

    showModal () {
        this.setState({
            showModal: true
        })
    }

    render() {
        return (
            <>
            <FadeTransform
                in transformProps = {{exitTransform: 'scale(2) TranslateY(-100'}}
            >
            <Modal modalTransition = {{transitionAppearTimeout: 700}} isOpen = {this.state.isModalOpen} toggle = {this.toggleModal}>
                <ModalHeader toggle = {this.toggleModal}>Hello</ModalHeader>
                <ModalBody>World</ModalBody>
            </Modal>
            </FadeTransform>
            <Button type = "button" onClick = {this.toggleModal}>OpenModal</Button>
            </>
        );
    }
};

export default Contact;
