import * as ActionTypes from './ActionTypes';

//lunch reducer function
export const Lunchs = (state = {
    isLoading : true,
    lunchs: [],
    errMsg: null
}, action) => {
    switch(action.type) {
        case ActionTypes.LUNCH_LOADING:
            return {
                ...state,
                isLoading: true,
                lunchs: [],
                errMsg: null
            }
        case ActionTypes.ADD_LUNCH:
            return {
                ...state,
                isLoading: false,
                lunchs: action.payload,
                errMsg: null
            }
        case ActionTypes.LUNCH_FAILED:
            return {
                ...state, 
                isLoading: false,
                lunchs: [],
                errMsg: action.payload
            }
        }
};

// ====> to configureStore
