import React, { Component } from 'react'

class Signin extends Component {
    state = {
        email: '',
        password: '',
        boxShadow: '2px 3px 8px 0px rgb(212, 195, 195)'
    };

    handleFocus = () => {
        this.setState({
            boxShadow: '2px 3px 8px 0px rgb(119, 117, 117)'
        })
    }
    handleBlur = () => {
        this.setState({
            boxShadow: '2px 3px 8px 0px rgb(212, 195, 195)'
        })
    }

    handleSubmit = e => {
        e.preventDefault();
        console.log(this.state);
    }

    handleChange = e => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }
    render() {
        return (
            <div className = "container form-container" style = {{boxShadow: this.state.boxShadow}} onFocus = {this.handleFocus} onBlur = {this.handleBlur}>
            <form className="white" onSubmit = {this.handleSubmit}>
                <h5 className="grey-text text-darken-3">Signin</h5>
                <div className="input-field">
                    <i className="prefix"><i className="fa fa-envelope"></i></i>
                    <label htmlFor="email">Email</label>
                    <input type="email" id = "email" onChange = {this.handleChange} className = "validate"/>
                    <span className = "helper-text" data-error = "Invalid Email!"></span>
                </div>
                <div className="input-field">
                    <i className="prefix"><i className="fa fa-key"></i></i>
                    <label htmlFor="password">Passwrod</label>
                    <input type="password" id = "password" onChange = {this.handleChange}/>
                </div>
                <div className="input-field">
                    <button className="btn pink lighten-1 z-depth-0">
                        Login
                    </button>
                </div>
            </form>
                
            </div>
        )
    }
}

export default Signin;
