import React from 'react';
import { Link } from 'react-router-dom/cjs/react-router-dom.min';
import { Breadcrumb, BreadcrumbItem, Card, CardBody, CardGroup, CardImg, CardTitle, Media } from 'reactstrap';

function RenderDishDetail ({item}) {
    return(
        <div className = "container">
            <div className = "row">
                <div className = "col-12 col-md-5 m-1">
                    <CardGroup>
                        <Card>
                            <CardImg src = {item.image} alt = {item.name} width = "100%"/>
                            <CardBody>
                                <CardTitle>{item.name}</CardTitle>
                            </CardBody>
                        </Card>
                    </CardGroup>
                </div>
                <div className = "col-12 col-md-5 m-1">
                    <Media>
                        <Media heading>{item.name}</Media>
                        <p>{item.price}</p>
                        <p>{item.description}</p>
                    </Media>
                </div>
            </div>
        </div>
    );
}

const DishDetail = (props) => {
    const itemDetail = props.item.map(item => {

        return(
            <>
                <RenderDishDetail key = {item.id} item = {item} />
            </>
        );
    });

    const item = props.item.map(item => item)[0];
    return (
        <div className = "container">
        <Breadcrumb>
        <BreadcrumbItem>
            <Link to = {`/menu/${item.category}`}>{item.category}</Link>
        </BreadcrumbItem>
        <BreadcrumbItem>{item.label}</BreadcrumbItem>
        </Breadcrumb>
        {itemDetail}
        </div>
    );
};

export default DishDetail;
