import React from 'react';
import { Link } from 'react-router-dom';
import { Card, CardImg, CardHeader, Breadcrumb, BreadcrumbItem, Col, CardTitle, CardText, CardBody, CardGroup} from 'reactstrap'
function RenderSpecificMenu({soup}) {
    return(
        <Card key = {soup.id}>
            <CardImg src = {soup.image} alt = {soup.name} width = "100%" />
            <CardBody>
            <CardTitle className = "text-center">
                {soup.name}
            </CardTitle>
            </CardBody>
        </Card>
    );
}

const SpecificMenu = (props) => {
    const menu = props.soups.soups.map(soup => {
        console.log(soup.id);

        return (
            <div className = "col-12 col-md-4 mb-2">
                <div key = {soup.id}> 
        <Link className = "card-link">

                <CardGroup>
                <RenderSpecificMenu soup = {soup}/>
                </CardGroup>
        </Link>
                </div>
            </div>
        );
    })
    return (
        <div className = "container">
        <div className = "row">
            <Breadcrumb>
                <BreadcrumbItem>
                    <Link to = "/menu">Menu</Link>
                </BreadcrumbItem>
                <BreadcrumbItem active>Soup Menu</BreadcrumbItem>
            </Breadcrumb>

            <div className = "col-12">
                <h3>Soup Menu</h3> <hr/>
            </div>
        </div>
        <div className = "row">
                {menu}
        </div>
        </div>
    );
}

export default SpecificMenu;
