import React from 'react';
import { Link } from 'react-router-dom';
import { Card, CardImg, CardHeader, Breadcrumb, BreadcrumbItem, Col, CardTitle, CardText, CardBody, CardGroup, CardDeck, CardImgOverlay} from 'reactstrap'
function RenderSpecificMenu({soup}) {
    return(
        <Link className = "card-link">
        <CardDeck>
        <Card key = {soup.id}>
            <CardImg top src = {soup.image} alt = {soup.name} width = "100%" />
            {/* <CardBody> */}
            <CardText className = "text-center soup-name mb-3">
                {soup.name}
            </CardText>
            {/* </CardBody> */}
        </Card>
        </CardDeck>
        </Link>
    );
}

const SpecificMenu = (props) => {
    const menu = props.soups.soups.map(soup => {
        console.log(soup.id);

        return (
            <div className = " m-2">
                <div key = {soup.id}>
                <RenderSpecificMenu soup = {soup}/>
                </div>
            </div>
        );
    })
    return (
        <div className = "container">
        <div className = "row">
            <Breadcrumb>
                <BreadcrumbItem>
                    <Link to = "/menu">Menu</Link>
                </BreadcrumbItem>
                <BreadcrumbItem active>Soup Menu</BreadcrumbItem>
            </Breadcrumb>

            <div className = "col-12">
                <h3>Soup Menu</h3> <hr/>
            </div>
        </div>
        <div className = "row">
                {menu}
        </div>
        </div>
    );
}

export default SpecificMenu;
