import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Breadcrumb, BreadcrumbItem, Card, CardImg, CardHeader } from 'reactstrap';

function RenderSpecificMenu({item}) {
    return (
        <div className = "row">
            <div className = "col-12 col-md-3">
                <Link className = "card-link">
                    <Card>
                        <CardImg src = {item.image} alt = {item.name} width = "100%" />
                        <CardHeader className = "text-center card-header">
                            {item.name}
                        </CardHeader>
                    </Card>
                </Link>
            </div>
        </div>
    );
}

function SpecificMenu(props) {
    return (
        <div className = "container">
            <Breadcrumb>
                <BreadcrumbItem>
                    <Link to = "/menu" active>Menu</Link>
                </BreadcrumbItem>
                <BreadcrumbItem>
                    Lunch Menu
                </BreadcrumbItem>
            </Breadcrumb>
            <RenderSpecificMenu 
                item = {props.lunch}
            />
        </div>
    );
}
export default SpecificMenu;
