import * as ActionTypes from './ActionTypes';
import { LUNCHS } from '../share/lunch-menu';
import { SOUPS } from '../share/soup-menu';

// LUNCH
export const fetchLunch = () => (dispatch) => {
    dispatch(lunchLoading (true));

    setTimeout(() => {
        dispatch(addLunch(LUNCHS));
    },2000)
}

export const lunchLoading = () => ({
    type: ActionTypes.LUNCH_LOADING,
});

export const addLunch = (lunchs) => ({
    type: ActionTypes.ADD_LUNCH,
    payload: lunchs
})
export const lunchFailed = (errMsg) => ({
    type: ActionTypes.LUNCH_FAILED,
    payload: errMsg
});

//SOUP
export const fetchSoup = () => (dispatch) => {
    dispatch(soupLoading (true));

    setTimeout(() => {
        dispatch(addSoup(SOUPS));
    },2000)
}

export const soupLoading = () => ({
    type: ActionTypes.SOUP_LOADING,
});

export const addSoup = (soups) => ({
    type: ActionTypes.ADD_SOUP,
    payload: soups
})
export const soupFailed = (errMsg) => ({
    type: ActionTypes.SOUP_FAILED,
    payload: errMsg
});

// ===> to reducers function(lunch.js)
