import React, { Component } from 'react';
import Header from './HeaderComponent';
import Home from './HomeComponent';
import Menu from './MenuComponent';
import Footer from './FooterComponent';
import LunchMenu from './LunchComponent';
import { Switch, Route, Redirect, withRouter } from 'react-router-dom';
import { connect } from 'react-redux'
import { fetchLunch } from '../redux/ActionCreates';

const mapStateToProps = state => {
    return {
        lunchs: state.lunchs
    }
}

const mapDispatchToProps = dispatch => ({
    fetchLunch: () => {dispatch(fetchLunch())}
});

class Main extends Component {
    constructor(props) {
        super(props);
    }
    
    componentDidMount() {
        this.props.fetchLunch();
    }

    render() {
        const MenuPage = () => {
            return (
                <Menu 
                    lunchLoading = {this.props.lunchs.isLoading}
                    lunch = {this.props.lunchs.lunchs.filter(lunch => lunch.featured)[0]}
                    lunchErrMsg = {this.props.lunchs.errMsg}
                />
            );
        }
        const MenuCategory = ({match}) => {
                return (
                    <LunchMenu 
                        lunch = {this.props.lunchs.lunchs.filter(lunch => lunch.category === match.params.specific_menu)}
                    />
                );
        };
        return (
            <div>
                <Header />
                {/* for routes */}
                <Switch>
                    <Route  path = "/home" component = {Home} />
                    <Route exact path = "/menu" component = {MenuPage} />
                    <Route path = "/menu/:specific_menu" component = {MenuCategory}/>
                    <Redirect to = "/home" />
                </Switch>
                <Footer />
            </div>
        );
    };
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Main));
