import React from 'react';
import { Link } from 'react-router-dom';
import { Card, CardImg, CardHeader, Breadcrumb, BreadcrumbItem} from 'reactstrap'
function RenderSpecificMenu({item}) {
    return(
        <Link className = "card-link">
        <Card>
            <CardImg src = {item.image} alt = {item.name} width = "100%" />
            <CardHeader className = "text-center card-header">
                {item.name}
            </CardHeader>
        </Card>
    </Link>
    );
}

const SpecificMenu = (props) => {
    const specific_menu = props.soups.soups.map(soup => {
        console.log(soup);

        return (
            <div className = "col-12 col-sm-3 col-md-4 mb-2">
                <RenderSpecificMenu item = {soup}/>
            </div>
        );
    })
    return (
        <div className = "container">
        <div className = "row">
            <Breadcrumb>
                <BreadcrumbItem>
                    <Link to = "/home">Home</Link>
                </BreadcrumbItem>
                <BreadcrumbItem active>Menu</BreadcrumbItem>
            </Breadcrumb>

            <div className = "col-12">
                <h3>Menu</h3> <hr/>
            </div>
        </div>
        <div className = "row">
                {specific_menu}
        </div>
        </div>
    );
}

export default SpecificMenu;
