import React from 'react';
import { Link } from 'react-router-dom';
import { Card, CardImg, Breadcrumb, BreadcrumbItem, CardTitle, CardBody, CardDeck, CardGroup} from 'reactstrap'
import { Loading } from './LoadingComponent';
function RenderSpecificMenu({isLoading, errMsg, item}) {
    if (isLoading) {
        return (
            <Loading />
        );
    }
    else if (errMsg) {
        return (
            <h4>{errMsg}</h4>
        );
    }
    else {
        return(
            <Link to = "/" className = "card-link">
            <CardBody>
                <Card>
                    <CardImg top src = {item.image} alt = {item.name} width = "100%" />
                    <CardBody>
                    <CardTitle className = "text-center soup-name p-1">
                        {item.name}
                    </CardTitle>
                    </CardBody>
                </Card>
            </CardBody>
            </Link>
        );
    }
}

const SpecificMenu = (props) => {
    const isLoading = props.isLoading;
    const errMsg = props.errMsg;
    const menu = props.items.map(item => {
        return (
            <div className = "col-12 col-md-5 m-1 mr-2" key = {item.id}>
                <RenderSpecificMenu 
                    item = {item}
                    isLoading = {isLoading}
                    errMsg = {errMsg}
                />
            </div>
        );
    });
    var breadcrumbName = props.items.map(item => item.category)[0];
    return (
        <div className = "container">
        <div className = "row">
            <Breadcrumb>
                <BreadcrumbItem>
                    <Link to = "/menu">Menu</Link>
                </BreadcrumbItem>
                <BreadcrumbItem active className = "text-capitalize">
                    {breadcrumbName}
                </BreadcrumbItem>
            </Breadcrumb>

            <div className = "col-12 text-uppercase text-center">
                <h3>{breadcrumbName}</h3> <hr/>
            </div>
        </div>
        <div className = "row">
                {menu}
        </div>
        </div>
    );
}

export default SpecificMenu;
