import React from 'react';
import { Breadcrumb, BreadcrumbItem, Card, CardBody, CardGroup, CardImg, CardTitle, Media } from 'reactstrap';

function RenderDishDetail ({item}) {
    return(
        <div className = "container">
            <div className = "row">
                <div className = "col-12 col-md-5 m-1">
                    <CardGroup>
                        <Card>
                            <CardImg src = {item.image} alt = {item.name} width = "100%"/>
                            <CardBody>
                                <CardTitle>{item.name}</CardTitle>
                            </CardBody>
                        </Card>
                    </CardGroup>
                </div>
                <div className = "col-12 col-md-5 m-1">
                    <Media>
                        <Media heading>{item.name}</Media>
                        <p>{item.price}</p>
                        <p>{item.description}</p>
                    </Media>
                </div>
            </div>
        </div>
    );
}

const DishDetail = (props) => {
    const item = props.item;
    return(
        <Breadcrumb>
            <BreadcrumbItem>{item.category}</BreadcrumbItem>
            <BreadcrumbItem>{item.label}</BreadcrumbItem>
        </Breadcrumb>
    );
};

export default DishDetail;
