import React, { Component } from 'react';
import { Navbar, NavbarBrand, NavbarToggler, Collapse, Nav, NavItem } from 'reactstrap';
import { NavLink } from 'react-router-dom';

class Header extends Component {
    constructor (props) {
        super(props);

        this.state = {
            isNavOpen: false
        };
        this.toggleNav = this.toggleNav.bind(this);
    }

    toggleNav () {
        this.setState ({
            isNavOpen: !this.state.isNavOpen
        })
    }
    render() {
        return (
            <>
            <Navbar dark expand = "md">
                <div className = "container">
                    <div>
                    <NavbarBrand href = "/">
                        <NavLink className = "nav-link" to = "/home">
                        <img src = "assets/imgs/logo.png" width = "80" height = "80" />
                        </NavLink>
                    </NavbarBrand>
                    <NavbarToggler onClick = {this.toggleNav}  />
                    </div>
                    <hr className = "bg-success visible-md" />
                    <Collapse navbar isOpen = {this.state.isNavOpen} className = "text-center">
                    <Nav navbar>
                        <NavItem>
                            <NavLink className = "nav-link" to = "/menu">
                                <span className = "fa fa-list fa-lg">
                                </span> Menu
                            </NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink className = "nav-link" to = "/about">
                                <span className = "fa fa-info-circle fa-lg">
                                </span> About
                            </NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink className = "nav-link" to = "/awards">
                                <span className = "fa fa-certificate fa-lg"></span>
                                 Awards
                            </NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink className = "nav-link" to = "/contact">
                                <span className = "fa fa-address-card fa-lg"></span> Contact
                            </NavLink>
                        </NavItem>
                    </Nav>
                    </Collapse>
                </div>
            </Navbar>
            </>
        );
    }
}

export default Header;
