import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Breadcrumb, BreadcrumbItem, Card, CardHeader, CardImg } from 'reactstrap';
import { Loading } from './LoadingComponent';

function RenderCard({isLoading, item, errMsg}) {
    if(isLoading) {
        return (
            <Loading />
        );
    }
    else if (errMsg) {
        return (
            <h4>{errMsg}</h4>
        );
    }
    else {
        return (
            <div className = "row">
                <div className = "col-12 col-md-4">
                    <Link to = "/" className = "card-link">
                        <Card>
                            <CardImg src = {item.image} alt = {item.category} width = "100%" />
                            <CardHeader className = "text-center card-header">
                                {item.category}
                            </CardHeader>
                        </Card>
                    </Link>
                </div>
            </div>
        );
    }
}

function Menu (props) {

        return (
            <div className = "container">
                <Breadcrumb>
                    <BreadcrumbItem>
                        <Link to = "/home">Home</Link>
                    </BreadcrumbItem>
                    <BreadcrumbItem active>
                        Menu
                    </BreadcrumbItem>
                </Breadcrumb>
                <RenderCard 
                    item = {props.lunch}
                    isLoading = {props.lunchLoading}
                    errMsg = {props.lunchErrMsg}
                />
            </div>
        );
    }

export default Menu;
