import React, { Component } from 'react';
import { connect } from 'react-redux';
import { createProject } from '../../redux/actions/projectActions';

const mapDispatchToProps = (dispatch) => {
    return {
        createProject: project => dispatch(createProject(project))
    }
}

class CreateProject extends Component {
    state = {
        project: {
            title: '',
            content: ''
        },
        boxShadow: '2px 3px 8px 0px rgb(212, 195, 195)'
    };

    handleFocus = () => {
        this.setState({
            boxShadow: '2px 3px 8px 0px rgb(119, 117, 117)'
        })
    }
    handleBlur = () => {
        this.setState({
            boxShadow: '2px 3px 8px 0px rgb(212, 195, 195)'
        })
    }
    handleSubmit = e => {
        e.preventDefault();
        console.log(this.state);
        this.props.createProject(this.state.project);
    }

    handleChange = e => {
        this.setState({
            project:{[e.target.id]: e.target.value}
        })
    }
    render() {
        return (
            <div className = "container form-container" style = {{boxShadow: this.state.boxShadow}} onFocus = {this.handleFocus} onBlur = {this.handleBlur}>
            <form onSubmit = {this.handleSubmit}>
                <h5 className="grey-text text-darken-3">Create New Project</h5>
                <div className="input-field">
                    <i className="prefix"><i className="fa fa-header"></i></i>
                    <label htmlFor="title">Title</label>
                    <input type="text" id = "title" onChange = {this.handleChange}/>
                    <span className = "helper-text" data-error = "Invalid Email!"></span>
                </div>
                <div className="input-field">
                    <i className="prefix"><i className="fa fa-file"></i></i>
                    <label htmlFor="content">Content</label>
                    <textarea className="materialize-textarea" id = "content" onChange = {this.handleChange}></textarea>
                </div>
                <div className="input-field">
                    <button className="btn pink lighten-1 z-depth-0">
                        Create
                    </button>
                </div>
            </form>
                
            </div>
        )
    }
}

export default connect(null, mapDispatchToProps)(CreateProject);
